import {
  Button,
  CircularProgress,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  TextField,
} from "@mui/material";
import "./homepage.css";
import axios from "axios";
import { useState } from "react";

const FRAVEGA =
  "data:image/png;base64,";
const MERCADOLIBRE =
  "https://pbs.twimg.com/profile_images/1609904580472496130/qEZkjppS_400x400.jpg";
const FULLHARD = "https://www.fullh4rd.com.ar/assets/logo_black.png";
const NOTFOUND =
  "https://www.google.com/url?sa=i&url=https%3A%2F%2Fes.m.wikipedia.org%2Fwiki%2FArchivo%3AImage-not-found.png&psig=AOvVaw3Cz3cdZBkT30doJnvOKo7-&ust=1700850111731000&source=images&cd=vfe&opi=89978449&ved=0CBIQjRxqFwoTCNCWzMfe2oIDFQAAAAAdAAAAABAT";

type Element = {
  name: string;
  price: number;
  url: string;
};

interface CardProps {
  element: Element;
}

const Card: React.FC<CardProps> = ({ element }) => {
  const [isHovered, setHovered] = useState(false);

  const handleCardClick = () => {
    window.open(element.url, "_blank", "noopener noreferrer");
  };

  const getLogoImage = () => {
    if (element.url.includes("fullh4rd")) {
      return FULLHARD;
    } else if (element.url.includes("mercadolibre")) {
      return MERCADOLIBRE;
    } else if (element.url.includes("fravega")) {
      return FRAVEGA;
    }
    return NOTFOUND; // Default or fallback image
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "space-between",
        padding: "10px",
        margin: "10px",
        width: "1000px",
        backgroundColor: isHovered ? "#aaf" : "#ddd",
        cursor: "pointer",
      }}
      onClick={handleCardClick}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <div>
        <span className="elementTitle">{element.name}</span>
        <br />
        <br />
        <span className="elementPrice">
          Precio: ${element.price.toLocaleString()}
        </span>
      </div>
      <img
        src={getLogoImage()}
        alt="Logo"
        style={{ width: "50px", height: "50px" }}
      />
    </div>
  );
};

export default function Homepage() {
  const handleSubmit = async () => {
    // armar url
    const url = `http://localhost:8080/api/general?ram=${ram}&inches=${screen}&storage=${storage}&minPrice=${minPrice}&maxPrice=${maxPrice}&sort=${sort}&limit=${amount}&processor=${processor}`;
    console.log(url);

    try {
      const data = await axios.get(url);
      console.log(data.data);
      setResults(data.data);
    } catch (e) {
      //
    }
  };

  const [maxPrice, setMaxPrice] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [amount, setAmount] = useState("");
  const [sort, setSort] = useState("");
  const [screen, setScreen] = useState("");
  const [ram, setRam] = useState("");
  const [storage, setStorage] = useState("");
  const [processor, setProcessor] = useState("");

  const [results, setResults] = useState<Element[]>([]);

  return (
    <div className="container">
      <h1 className="title">Web Scraper</h1>
      <div className="inputs-container">
        {/* First Row */}
        <div className="input-row">
          <div className="input-item">
            <span className="label">Max Price</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setMaxPrice(e.target.value)}
            />
          </div>

          <div className="input-item">
            <span className="label">Min Price</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setMinPrice(e.target.value)}
            />
          </div>

          <div className="input-item">
            <span className="label">Min Screen Size</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setAmount(e.target.value)}
            />
          </div>

          <div className="input-item">
            <span className="label">Max Screen Size</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setSort(e.target.value)}
            />
          </div>
        </div>

        {/* Second Row */}
        <div className="input-row">
          <div className="input-item">
            <span className="label">Min Ram</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setRam(e.target.value)}
            />
          </div>

          <div className="input-item">
            <span className="label">Max Ram</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setScreen(e.target.value)}
            />
          </div>

          <div className="input-item">
            <span className="label">Min Storage</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setStorage(e.target.value)}
            />
          </div>

          <div className="input-item">
            <span className="label">Max Storage</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setProcessor(e.target.value)}
            />
          </div>
        </div>
        {/* Third Row */}
        <div className="input-row">
          <div className="input-item">
            <span className="label">Limit</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setRam(e.target.value)}
            />
          </div>

          <div className="input-item">
            <span className="label">Sort</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setScreen(e.target.value)}
            />
          </div>

          <div className="input-item">
            <span className="label">Processor</span>
            <FormControl fullWidth>
              <InputLabel>Proccesor</InputLabel>
              <Select
                value={processor}
                onChange={(e) => setProcessor(e.target.value)}
              >
                <MenuItem value={""}>Any</MenuItem>
                <MenuItem value={"intel"}>Intel</MenuItem>
                <MenuItem value={"amd"}>Amd</MenuItem>
                <MenuItem value={"apple"}>Apple</MenuItem>
              </Select>
            </FormControl>
          </div>

          {/* <div className="input-item">
            <span className="label">Processor</span>
            <TextField
              variant="filled"
              className="textfield"
              onChange={(e) => setProcessor(e.target.value)}
            />
          </div> */}
        </div>
      </div>
      <div style={{ flexDirection: "row", marginBottom: 20 }}>
        <Button
          style={{ marginRight: 10 }}
          variant="contained"
          onClick={handleSubmit}
        >
          Submit
        </Button>

        <Button variant="contained" onClick={() => setResults([])}>
          Clear
        </Button>
      </div>

      {results && results.length ? (
        <div>
          <div
            style={{
              overflowY: "scroll",
              maxHeight: "450px",
            }}
          >
            {results.map((element, index) => (
              <Card key={index} element={element} />
            ))}
          </div>
        </div>
      ) : (
        <div style={{ marginTop: 50 }}>
          <CircularProgress />
        </div>
      )}
    </div>
  );
}
